mapper.select:
SELECT COUNT(admin_id)
FROM joonggodeal.admin_info
WHERE admin_id = #{id}

mapper.update:
UPDATE member
SET is_active = '0'
WHERE member_id = #{member_id}
        AND member_name = #{member_name}
        AND is_active = '1'

mapper.select:
SELECT member_id
FROM member
WHERE member_name = #{userName}
  AND member_phone_number = #{phonenumber}

SELECT COUNT(*)
FROM member
WHERE member_id = #{userID}
  AND member_phone_number = #{phonenumber}

mapper.update:
UPDATE member
SET member_password = #{newPassword}
WHERE member_id = #{userID}
